"use client"

import * as React from "react"
import { cva, type VariantProps } from "class-variance-authority"
import { cn } from "@/lib/utils"

const buttonVariants = cva(
  [
    "inline-flex items-center justify-center gap-2",
    "rounded-lg font-medium",
    "transition-all duration-200",
    "focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-ring focus-visible:ring-offset-2 focus-visible:ring-offset-background",
    "disabled:pointer-events-none disabled:opacity-50",
  ],
  {
    variants: {
      variant: {
        aurora: [
          "relative overflow-hidden",
          "bg-gradient-to-r from-[rgb(var(--ap-aurora-1))] via-[rgb(var(--ap-aurora-2))] to-[rgb(var(--ap-aurora-3))]",
          "text-primary-foreground",
          "shadow-lg",
          "hover:shadow-[0_0_24px_rgba(var(--ap-primary),0.4)]",
          "before:absolute before:inset-0",
          "before:bg-gradient-to-r before:from-transparent before:via-white/20 before:to-transparent",
          "before:translate-x-[-200%]",
          "hover:before:translate-x-[200%]",
          "before:transition-transform before:duration-700",
        ],
        glass: [
          "bg-background/60 backdrop-blur-md",
          "border border-border/50",
          "text-foreground",
          "hover:bg-background/80",
          "hover:border-primary/50",
          "hover:shadow-[0_0_16px_rgba(var(--ap-primary),0.2)]",
        ],
        ghost: [
          "bg-transparent",
          "text-foreground",
          "hover:bg-muted/50",
          "hover:text-primary",
        ],
      },
      size: {
        sm: "h-8 px-3 text-sm",
        md: "h-10 px-4 text-sm",
        lg: "h-12 px-6 text-base",
      },
      motion: {
        pop: "",
        float: "animate-ap-float",
        shine: "",
        snap: "",
        bounce: "",
        wiggle: "",
        jelly: "",
        none: "",
      },
    },
    defaultVariants: {
      variant: "aurora",
      size: "md",
      motion: "pop",
    },
  }
)

export interface ButtonProps
  extends React.ButtonHTMLAttributes<HTMLButtonElement>,
    VariantProps<typeof buttonVariants> {
  leftIcon?: React.ReactNode
  rightIcon?: React.ReactNode
}

const Button = React.forwardRef<HTMLButtonElement, ButtonProps>(
  (
    {
      className,
      variant,
      size,
      motion = "pop",
      leftIcon,
      rightIcon,
      children,
      ...props
    },
    ref
  ) => {
    const [activeAnimation, setActiveAnimation] = React.useState<string | null>(null)

    const handleClick = (e: React.MouseEvent<HTMLButtonElement>) => {
      // Trigger animation based on motion type
      switch (motion) {
        case "pop":
          setActiveAnimation("animate-ap-pop")
          setTimeout(() => setActiveAnimation(null), 200)
          break
        case "bounce":
          setActiveAnimation("animate-ap-bounce")
          setTimeout(() => setActiveAnimation(null), 300)
          break
        case "wiggle":
          setActiveAnimation("animate-ap-wiggle")
          setTimeout(() => setActiveAnimation(null), 500)
          break
        case "jelly":
          setActiveAnimation("animate-ap-jelly")
          setTimeout(() => setActiveAnimation(null), 300)
          break
      }
      props.onClick?.(e)
    }

    const handleMouseDown = (e: React.MouseEvent<HTMLButtonElement>) => {
      if (motion === "snap") {
        const button = e.currentTarget
        button.style.transform = "scale(0.97)"
      }
      props.onMouseDown?.(e)
    }

    const handleMouseUp = (e: React.MouseEvent<HTMLButtonElement>) => {
      if (motion === "snap") {
        const button = e.currentTarget
        button.style.transform = "scale(1)"
      }
      props.onMouseUp?.(e)
    }

    const handleMouseLeave = (e: React.MouseEvent<HTMLButtonElement>) => {
      if (motion === "snap") {
        const button = e.currentTarget
        button.style.transform = "scale(1)"
      }
      props.onMouseLeave?.(e)
    }

    return (
      <button
        className={cn(
          buttonVariants({ variant, size, motion }),
          activeAnimation,
          motion === "shine" && "animate-ap-shine bg-[length:200%_100%]",
          className
        )}
        ref={ref}
        onClick={handleClick}
        onMouseDown={handleMouseDown}
        onMouseUp={handleMouseUp}
        onMouseLeave={handleMouseLeave}
        {...props}
      >
        {leftIcon && <span className="shrink-0">{leftIcon}</span>}
        {children}
        {rightIcon && <span className="shrink-0">{rightIcon}</span>}
      </button>
    )
  }
)

Button.displayName = "Button"

export { Button, buttonVariants }
